import React from 'react'
import { Route, useParams } from 'react-router-dom';

import Comments from '../components/comments/Comments';
import HighlightedQuote from '../components/quotes/HighlightedQuote';
import NoQuotesFound from '../components/quotes/NoQuotesFound'

const DUMMY_DATA = [
    {
        id: 'q1',
        author: 'Marise',
        text: 'Learnig GOOGLE UX'
    },
    {
        id: 'q2',
        author: 'Umair',
        text: 'Learnig IT Automation'
    },
    {
        id: 'q3',
        author: 'Digiksil',
        text: 'Learnig Amazon VA'
    },
    {
        id: 'q4',
        author: 'Udmey',
        text: 'Learnig Javascript'
    },
];

const DetailQuotes = function (){
    const params = useParams()
    const quote = DUMMY_DATA.find((quote)=> quote.id === params.quotesId);
    // if(!quote){
    //     return <p>NO QUOTES FOUND!</p>
    // }

    return ( 
    <React.Fragment>
        {/* {!quote && <NoQuotesFound/>} */}
        {quote && <HighlightedQuote text={quote.text } author={quote.author}/>}
        {/* <Route path="/quotes/Params/comments"><Comments/></Route> */}
        <Route path={`/quotes/${params.quotesId}/comments`}><Comments/></Route>

    </React.Fragment>
    
    );
};


export default DetailQuotes;
