        id: 'q1',
        author: 'Marise',
        text: 'Learnig GOOGLE UX'
    },
    {
        id: 'q2',
        author: 'Umair',
        text: 'Learnig IT Automation'
    },
    {
        id: 'q3',
        author: 'Digiksil',
        text: 'Learnig Amazon VA'
    },
    {
        id: 'q4',
        author: 'Udmey',
        text: 'Learnig Javascript'
    },
];

const DetailQuotes = function (){
    const params = useParams()
    const quote = DUMMY_DATA.find((quote)=> quote.id === params.id);
    console.log(params.quotesId);

    return ( 
    <React.Fragment>
        <h1>Detail Quotes Page</h1>
        <p>Quotes ID {params.quotesId}</p>
        {/* <Route path="/quotes/Params/comments"><Comments/></Route> */}
        <Route path={`/quotes/${params.quotesId}/comments`}><Comments/></Route>

    </React.Fragment>
    
    );
};


export default DetailQuotes;
